mapper.select:
SELECT COUNT(commentnumber) AS commentCnt  
FROM board_comment
WHERE postnumber = #{postNumber}

SELECT TT1.rnum as num, TT1.commentnumber, TT1.nickname, TT1.content, CASE WHEN TO_CHAR(TT1.writtendate,'YYYYMMDD') = TO_CHAR(sysdate,'YYYYMMDD') THEN TO_CHAR(TT1.writtendate,'HH24:MI') ELSE TO_CHAR(TT1.writtendate,'YYYY/MM/DD') END AS writtendate, TT1.postnumber, TT1.memberid, B.totalCnt, COALESCE(C.likes, 0) AS likes FROM ( SELECT ROWNUM AS rnum, T1.commentnumber, T1.nickname, T1.content, T1.writtendate, T1.postnumber, T1.memberid FROM ( SELECT * FROM board_comment WHERE postnumber = #{postNumber} ORDER BY writtendate DESC ) T1
WHERE ROWNUM <= #{pageSize} * (#{pageNo}-1) + #{pageSize}
) TT1 CROSS JOIN ( SELECT COUNT(commentnumber) totalCnt FROM board_comment WHERE postnumber = #{postNumber} ) B LEFT JOIN ( SELECT bc.commentnumber, COUNT(l.typenumber) AS likes FROM board_comment bc LEFT JOIN likes l ON l.likestype = 20 AND l.typenumber = bc.commentnumber WHERE bc.postnumber = #{postNumber} GROUP BY bc.commentnumber ) C ON TT1.commentnumber = C.commentnumber
WHERE TT1.rnum >= #{pageSize} * (#{pageNo}-1) + 1

mapper.update:
UPDATE board_comment           
SET content = #{content},      
    postnumber = #{postNumber} 
WHERE commentnumber = #{commentNumber}


UPDATE board_comment
 SET likes  = NVL(#{likes} ,0)+1
 WHERE commentnumber  = #{commentNumber}
