mapper.insert:

    SELECT paymentinfoseq.nextval FROM DUAL

	INSERT INTO payment_information (
	  paymentinfoseq
	  , merchant_uid
	  , name
	  , amount
	  , buyer_email
	  , buyer_name
	  , buyer_tel
	) VALUES (
		#{paymentinfoseq}
	  , #{merchantUid}
	  , #{name}
	  , #{amount}
	  , #{buyerEmail}
	  , #{buyerName}
	  , #{buyerTel}
	)

mapper.update:
    UPDATE payment_information
		SET  merchant_uid = #{merchantUid}
		WHERE
		buyer_email = #{buyerEmail}
