mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.rnum as num, TT1.title, TT1.categoryNumber, TT1.views, --당일 등록이면 : 03:45 그렇치 않으면 2023/07/29 DECODE( TO_CHAR(TT1.writtendate,'YYYYMMDD'),TO_CHAR(sysdate,'YYYYMMDD') ,TO_CHAR(TT1.writtendate,'HH24:MI') ,TO_CHAR(TT1.writtendate,'YYYY/MM/DD') )writtendate, TT1.NICKNAME, TT1.POSTNUMBER, TT1.content FROM( SELECT ROWNUM as rnum, T1.* FROM( SELECT * FROM board WHERE categoryNumber = #{categoryNumber}
ORDER BY writtendate DESC )T1 WHERE ROWNUM <= #{pageSize} * (#{pageNo}-1)+#{pageSize} )TT1 WHERE rnum >=#{pageSize} * (#{pageNo}-1)+ 1 )A CROSS JOIN ( SELECT COUNT(postnumber) totalCnt FROM board WHERE categoryNumber = #{categoryNumber}
)B

mapper.update:

UPDATE board
 SET views  = NVL(#{views} ,0)+1
 WHERE postnumber  = #{postNumber}


UPDATE board
 SET likes  = NVL(#{likes} ,0)+1
 WHERE POSTNUMBER  = #{postNumber}

UPDATE board           
SET title    = #{title},      
    content = #{content},      
    categorynumber  = #{categoryNumber}
WHERE postnumber = #{postNumber}
